import React, { useState, useEffect } from "react";
import './App.css';

function App() {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [grade, setGrade] = useState("");
  const [editId, setEditId] = useState(null);
  useEffect(() => { const saved = localStorage.getItem("students");
    if (saved) setStudents(JSON.parse(saved));}, []);
  useEffect(() => {localStorage.setItem("students", JSON.stringify(students));}, [students]);
  const handleSubmit = (e) =>{ e.preventDefault();
    if (!name || !age || !grade) return;
    if (editId) {setStudents(students.map((s) => s.id === editId ? { id: editId, name, age, grade } : s ));
      setEditId(null);} 
    else { setStudents([...students, { id: Date.now(), name, age, grade }]);
    }
     setName("");
     setAge("");
     setGrade("");
  };
  const handleEdit = (stu) => {
    setName(stu.name);
    setAge(stu.age);
    setGrade(stu.grade);
    setEditId(stu.id);
  };
  const handleDelete = (id) => {
    setStudents(students.filter((s) => s.id !== id));
  };
  return (
    <div style={{ maxWidth: "500px", margin: "auto", padding: "20px" }}>
      <h1>Student Dashboard</h1>

      <form onSubmit={handleSubmit}>
        <input
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          placeholder="Age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
        />
        <input
          placeholder="Grade"
          value={grade}
          onChange={(e) => setGrade(e.target.value)}
        />
        <button type="submit">{editId ? "Update" : "Add"}</button>
      </form>
      <ul>
        {students.map((s) => (
          <li key={s.id}>
            <table border="1">
            <tr>
                Name
              </tr>
              <td>
                {s.name}
              </td>
              <tr>
                Age
              </tr>
              <td>
                {s.age}
              </td>
              <tr>
                Grade
              </tr>
              <td>
                {s.grade}
              </td>
            </table>
            {s.name} | Age: {s.age} | Grade: {s.grade}
            <button onClick={() => handleEdit(s)}>Edit</button>
            <button onClick={() => handleDelete(s.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
